    Verse(id: 'v192', text: 'Be anxious for nothing, but in everything by prayer and supplication, with thanksgiving, let your requests be made known to God.', reference: 'Philippians 4:6', book: 'Philippians', chapter: 4, verseNumber: 6, topics: ['mental-health', 'prayer', 'anxiety', 'bible-verses']),
    Verse(id: 'v193', text: 'Humble yourselves, therefore, under God\'s mighty hand, that he may lift you up in due time.', reference: '1 Peter 5:6', book: '1 Peter', chapter: 5, verseNumber: 6, topics: ['mental-health', 'hope', 'god', 'bible-verses']),
    Verse(id: 'v194', text: 'Search me, God, and know my heart; test me and know my anxious thoughts.', reference: 'Psalm 139:23', book: 'Psalms', chapter: 139, verseNumber: 23, topics: ['mental-health', 'prayer', 'god', 'bible-verses']),
    Verse(id: 'v195', text: 'Even though I walk through the darkest valley, I will fear no evil, for you are with me.', reference: 'Psalm 23:4', book: 'Psalms', chapter: 23, verseNumber: 4, topics: ['mental-health', 'peace', 'god', 'bible-verses']),
    Verse(id: 'v196', text: 'The Lord is near. Do not be anxious about anything.', reference: 'Philippians 4:5-6', book: 'Philippians', chapter: 4, verseNumber: 5, topics: ['mental-health', 'peace', 'god', 'bible-verses']),
