    Verse(id: 'v437', text: 'Blessed are the peacemakers, for they will be called children of God.', reference: 'Matthew 5:9', book: 'Matthew', chapter: 5, verseNumber: 9, topics: ['peace', 'kindness', 'god', 'bible-verses']),
    Verse(id: 'v438', text: 'Blessed are those who are persecuted because of righteousness, for theirs is the kingdom of heaven.', reference: 'Matthew 5:10', book: 'Matthew', chapter: 5, verseNumber: 10, topics: ['faith', 'hope', 'strength', 'bible-verses']),
