    // ==================== THANKSGIVING & PRAISE ====================
    Verse(id: 'v446', text: 'Give thanks to the Lord, for he is good; his love endures forever.', reference: 'Psalm 107:1', book: 'Psalms', chapter: 107, verseNumber: 1, topics: ['gratitude', 'love', 'god', 'bible-verses']),
