    Verse(id: 'v455', text: 'Praise the Lord. Praise God in his sanctuary; praise him in his mighty heavens.', reference: 'Psalm 150:1', book: 'Psalms', chapter: 150, verseNumber: 1, topics: ['gratitude', 'god', 'bible-verses']),

    // ==================== OVERCOMING FEAR ====================
    Verse(id: 'v456', text: 'So do not fear, for I am with you; do not be dismayed, for I am your God.', reference: 'Isaiah 41:10', book: 'Isaiah', chapter: 41, verseNumber: 10, topics: ['courage', 'god', 'peace', 'bible-verses']),
    Verse(id: 'v457', text: 'There is no fear in love. But perfect love drives out fear.', reference: '1 John 4:18', book: '1 John', chapter: 4, verseNumber: 18, topics: ['love', 'courage', 'peace', 'bible-verses']),
    Verse(id: 'v458', text: 'When anxiety was great within me, your consolation brought me joy.', reference: 'Psalm 94:19', book: 'Psalms', chapter: 94, verseNumber: 19, topics: ['mental-health', 'joy', 'comfort', 'bible-verses']),
