    Verse(id: 'v464', text: 'Do not be afraid of those who kill the body but cannot kill the soul.', reference: 'Matthew 10:28', book: 'Matthew', chapter: 10, verseNumber: 28, topics: ['courage', 'faith', 'bible-verses']),
    Verse(id: 'v465', text: 'Peace I leave with you; my peace I give you. Do not let your hearts be troubled and do not be afraid.', reference: 'John 14:27', book: 'John', chapter: 14, verseNumber: 27, topics: ['peace', 'courage', 'inner-peace', 'bible-verses']),

    // ==================== OBEDIENCE & FAITHFULNESS ====================
    Verse(id: 'v466', text: 'If you love me, keep my commands.', reference: 'John 14:15', book: 'John', chapter: 14, verseNumber: 15, topics: ['love', 'faith', 'bible-verses']),
