    // ==================== GUIDANCE ====================
    Verse(id: 'v471', text: 'Your word is a lamp for my feet, a light on my path.', reference: 'Psalm 119:105', book: 'Psalms', chapter: 119, verseNumber: 105, topics: ['faith', 'god', 'bible-verses']),
