    Verse(id: 'v484', text: 'No temptation has overtaken you except what is common to mankind. And God is faithful.', reference: '1 Corinthians 10:13', book: '1 Corinthians', chapter: 10, verseNumber: 13, topics: ['faith', 'strength', 'god', 'bible-verses']),
    Verse(id: 'v485', text: 'The Lord is near to all who call on him, to all who call on him in truth.', reference: 'Psalm 145:18', book: 'Psalms', chapter: 145, verseNumber: 18, topics: ['god', 'faith', 'bible-verses']),
    Verse(id: 'v486', text: 'Call to me and I will answer you and tell you great and unsearchable things you do not know.', reference: 'Jeremiah 33:3', book: 'Jeremiah', chapter: 33, verseNumber: 3, topics: ['god', 'faith', 'hope', 'bible-verses']),
    Verse(id: 'v487', text: 'The Lord watches over all who love him.', reference: 'Psalm 145:20', book: 'Psalms', chapter: 145, verseNumber: 20, topics: ['god', 'love', 'bible-verses']),
