    Verse(id: 'v489', text: 'Every word of God is flawless; he is a shield to those who take refuge in him.', reference: 'Proverbs 30:5', book: 'Proverbs', chapter: 30, verseNumber: 5, topics: ['god', 'trust', 'faith', 'bible-verses']),
    Verse(id: 'v490', text: 'Heaven and earth will pass away, but my words will never pass away.', reference: 'Matthew 24:35', book: 'Matthew', chapter: 24, verseNumber: 35, topics: ['god', 'faith', 'hope', 'bible-verses']),

    // ==================== LOVE FOR OTHERS ====================
