    // ==================== GENEROSITY ====================
    Verse(id: 'v506', text: 'Give, and it will be given to you. A good measure, pressed down, shaken together and running over.', reference: 'Luke 6:38', book: 'Luke', chapter: 6, verseNumber: 38, topics: ['kindness', 'hope', 'bible-verses']),
