    // ==================== PERSEVERANCE ====================
    Verse(id: 'v516', text: 'Let us not become weary in doing good, for at the proper time we will reap a harvest if we do not give up.', reference: 'Galatians 6:9', book: 'Galatians', chapter: 6, verseNumber: 9, topics: ['hope', 'strength', 'bible-verses']),
    Verse(id: 'v517', text: 'I have fought the good fight, I have finished the race, I have kept the faith.', reference: '2 Timothy 4:7', book: '2 Timothy', chapter: 4, verseNumber: 7, topics: ['faith', 'strength', 'hope', 'bible-verses']),
    Verse(id: 'v518', text: 'Therefore, since we are surrounded by such a great cloud of witnesses, let us throw off everything that hinders and the sin that so easily entangles.', reference: 'Hebrews 12:1', book: 'Hebrews', chapter: 12, verseNumber: 1, topics: ['faith', 'strength', 'letting-go', 'bible-verses']),
