    Verse(id: 'v580', text: 'With God we will gain the victory, and he will trample down our enemies.', reference: 'Psalm 60:12', book: 'Psalms', chapter: 60, verseNumber: 12, topics: ['god', 'strength', 'hope', 'bible-verses']),

    // ==================== FINAL VERSES ====================
    Verse(id: 'v581', text: 'The Lord bless you and keep you; the Lord make his face shine on you and be gracious to you.', reference: 'Numbers 6:24-25', book: 'Numbers', chapter: 6, verseNumber: 24, topics: ['god', 'grace', 'hope', 'bible-verses']),
